$(function() {


    $('body').on('click', '.page-scroll a', function(event) {
        var $anchor = $(this);

        if($(this).attr('href')!==undefined) {
            $('html, body').stop().animate({
                scrollTop: $($anchor.attr('href')).offset().top-100
            }, 1500, 'easeInOutExpo');
        }
        
        event.preventDefault();
    });
});

